(function($){'use strict';$(document).ready(function(){$('#mapplic').mapplic({source:'http://revox.io/json/countries.json',height:'100%',search:false,sidebar:false,minimap:true,locations:true,deeplinking:true,fullscreen:true,hovertip:false,maxscale:4,animate:true});$('.clear-map').click(function(){$('.mapplic-clear-button').trigger('click');});$.getJSON('http://revox.io/json/countries.json',function(data){var countryList=[];$.each(data.levels[0].locations,function(key,val){countryList.push({id:val.id,text:val.title});});$("#country-list").select2({data:countryList,width:"240px"});});$('#country-list').change(function(){var sel=$('#country-list').select2('data');window.location.hash=sel.id;});});})(window.jQuery);